クロージャでつくったカウンター。
function createCounter () {
    var i = 0;
    return function () {
        return ++i;
    };
};

var counter1 = createCounter();
var counter2 = createCounter();
alert(counter1()); //=> 1
alert(counter1()); //=> 2
alert(counter2()); //=> 1
alert(counter2()); //=> 2

