/**
 * Bookmark のコレクション
 * Bookmark 一覧に変化があったら 'change' イベントを発火する
 */
class Bookmarks {

    constructor () {
        this.bookmarks = [];
    }

    /**
     * @param {Bookmark} bookmark
     */
    push (bookmark) {
        this.bookmarks.push(bookmark);
        $(this).trigger('change');
    }

    /**
     * @param {function} callback
     */
    forEach (callback) {
        this.bookmarks.forEach(callback);
    }

}

