package Inter::Diary::Model::User;
use strict;
use warnings;

use JSON::Types ();
use Class::Accessor::Lite (
    ro => [qw(user_id name)],
    new => 1,
);

sub json_hash {
    my ($self) = @_;
    return +{
        user_id => JSON::Types::number $self->user_id,
        name    => JSON::Types::string $self->name,
    };
}

require JSON::XS;
